const emails = $input.all().map(item => item.json);

// ğŸ”¢ Extrai os emails corretamente do caminho message.content
const extracted = emails.map(e => {
  const c = e.message?.content || {};
  return {
    from: c.from || '[sem remetente]',
    to: c.to || '[sem destinatÃ¡rio]',
