for (const email of emailsSorted) {
  formattedContext += `[${email.date}] ${email.from} â†’ ${email.to} | ${email.subject}\n${email.body}\n\n---\n\n`;
}
formattedContext = formattedContext.trim();

// ğŸ“Œ Email mais recente
